mapper.select:
SELECT id, collect_form_id AS collectFormId, employee_id AS employeeId, state, message, holder_id AS holderId,
apply_date AS applyDate, handle_date AS handleDate
FROM df_collect_form_edit_apply WHERE employee_id = #{employeeId} AND collect_form_id = #{collectFormId}

SELECT apply.id AS applyId, apply.collect_form_id AS applyCollectFormId, apply.employee_id AS applyEmployeeId, apply.state AS applyState, apply.message AS applyMessage, apply.holder_id AS applyHolderId, apply.apply_date AS applyDate, apply.handle_date AS applyHandleDate, applyUser.id AS applyUserId, applyUser.name AS applyUserName, holder.id AS holderId, holder.name AS holderName, form.id AS formId, form.name AS formName FROM df_collect_form_edit_apply AS apply LEFT JOIN df_user AS applyUser ON apply.employee_id = applyUser.id LEFT JOIN df_user AS holder ON apply.holder_id = holder.id LEFT JOIN df_collect_form AS collect_form ON apply.collect_form_id = collect_form.id LEFT JOIN df_dynamic_form AS form ON collect_form.form_id = form.id WHERE 1 = 1
AND apply.employee_id = #{applyUserId}
AND apply.holder_id = #{holderId}
ORDER BY apply.apply_date DESC
